document.addEventListener("DOMContentLoaded",function(){const replacementKbds=document.querySelectorAll(".wcai-replacements-grid kbd");if(!replacementKbds.length)return;async function copyToClipboard(text,element){try{await navigator.clipboard.writeText(text);element.classList.add("copied");element.classList.add("wcai-pop");setTimeout(()=>{element.classList.remove("copied");element.classList.remove("wcai-pop")},3e3)}catch(err){console.error("Failed to copy text:",err)}}replacementKbds.forEach(kbd=>{kbd.addEventListener("click",async function(){const replacement=this.dataset.replacement;await copyToClipboard(replacement,this)})})});